import React, { InputHTMLAttributes } from 'react';

export function getButtonMock<TProps>() {
	return jest.fn<JSX.Element, [TProps]>(() => <div>Mock</div>);
}

export const StyledButtons = {
	Button: {
		Base: getButtonMock<InputHTMLAttributes<HTMLButtonElement>>(),
		Primary: getButtonMock<InputHTMLAttributes<HTMLButtonElement>>(),
		Secondary: getButtonMock<InputHTMLAttributes<HTMLButtonElement>>(),
		Rounded: {
			Primary: getButtonMock<InputHTMLAttributes<HTMLButtonElement>>(),
			Secondary: getButtonMock<InputHTMLAttributes<HTMLButtonElement>>(),
		},
		Square: getButtonMock<InputHTMLAttributes<HTMLButtonElement>>(),
	},
};
